import React from 'react';
import { Academic, Social, Normal } from './components/prop'
import { TopButton } from "./components/prompt";
import Result from './components/result';
import Basic from './components/basic'
import './App.css';

class App extends React.Component {
        constructor(props) {
            super(props);
            this.state = {
                ser: 0,
                point: 20
            }
            this.callback = {
                cost: this.changePoint.bind(this),
                submit: this.handleSubmit.bind(this)
            }
            this.pageNum = 5;
        }


        changeSer(e) {
            window.scrollTo(0, 0)
            if (this.state.ser > this.pageNum || this.state.ser < 0) return;
            if (e.target.value === 'add')
                this.setState({ ser: this.state.ser + 1 });
            else this.setState({ ser: this.state.ser - 1 });
        }

        changePoint(e, num) {
            if (e.target.value === 'add') {
                if (this.state.point == 0) return 0; //如果总点数为0则减小操作无效
                else {
                    this.setState({ point: this.state.point - 1 });
                    return 1;
                }
            } else if (e.target.value === 'sub') {
                if (!num) return 0; //如果对象剩余点数为0则增加操作无效
                else {
                    this.setState({ point: this.state.point + 1 });
                    return -1;
                }
            }
        }

        handleSubmit(data, name) {
            this.setState({
                [name]: data });
        }

        render() {
                var temp = [ <
                    Basic submit = { this.handleSubmit.bind(this) }
                    />, <
                    Academic callback = { this.callback }
                    />, <
                    Social callback = { this.callback }
                    />, <
                    Normal callback = { this.callback }
                    />, <Result data={this.state} / >
                ];
                return ( <
                    div className = "App" >
                    <
                    div className = 'inputArea' >
                    <
                    div className = 'operArea' > { temp[this.state.ser] } <
                    /div> <
                    div className = 'buttonArea' > {
                        this.state.ser > 0 &&
                        < button className = 'button button-left'
                        value = 'sub'
                        onClick = { this.changeSer.bind(this) } > 上一步 < /button>} {
                            this.state.ser < 4 &&
                                < button className = 'button button-right'
                            value = 'add'
                            onClick = { this.changeSer.bind(this) } > 下一步 < /button>} <
                                /div> <
                                /div> <
                                TopButton / >
                                <
                                /div>
                        );
                    }
                }

                export default App;
