import React, { createRef } from 'react'
import sha256 from 'js-sha256'
import './CSS/basic.css'

class Basic extends React.Component {
    static temp = {};
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            occupation: '',
            character: '',
            background: '',
        };
        this.submit = props.submit;
        this.fileInput = createRef();
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleImage = this.handleImage.bind(this);
    }

    handleSubmit(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleImage(e) {
        let src = window.URL.createObjectURL(e.target.files[0]);
        this.fileInput.current.src = src;
    }

    componentDidMount() {
        this.setState(Basic.temp);
    }

    componentWillUnmount() {
        for (let i in this.state)
            Basic.temp[i] = this.state[i];
        this.submit(this.state, 'basic');
        //上传basic数据
    }

    geneInput(title, name, sta = 0) {
        let temp;
        if (sta === 0) temp = <input className='input' name={name} value={this.state[name]} onChange={this.handleSubmit} />;
        if (sta === 1) temp = <textarea className='input textarea' name={name} value={this.state[name]} onChange={this.handleSubmit} />;
        return (<div className='inputField' key={sha256(name)}>
            <div className='itemName'>{title}：</div>
            {temp}
        </div>);
    }

    render() {
        return (
            <div className='basicInput'>
                <div className='title'>基本信息</div>

                {[this.geneInput('调查员姓名', 'name'),
                this.geneInput('职业', 'occupation'),
                this.geneInput('性格特征', 'character'),
                this.geneInput('背景故事', 'background', 1)]}

                <div className='inputField'>
                    <div className='itemName'>头像：</div>
                    <div>
                        <input
                            type='file'
                            accept='image/*' className='input imgInput' name='image'
                            onChange={this.handleImage}
                        ><img id='preview' ref={this.fileInput}></img></input>
                        
                    </div>
                </div>


            </div>
        );
    }
}

export default Basic;
