import React from 'react'
import Dropzone from 'react-dropzone';

class Basic extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            occupation: '',
            character: '',
            background: '',
        };
        this.temp = {};
        this.submit = props.submit;
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleImage = this.handleImage.bind(this);
    }

    handleSubmit(event) {
        this.setState({ [event.target.name]: event.target.value })
    }
    handleImage(event) {
        let src = window.URL.createObjectURL(event.target.files[0]);
        this.fileInput.current.src = src;
    }

    componentDidMount(){
        console.log(this.temp);
        this.setState(this.temp);
        console.log(1);
    }

    componentWillUnmount() {
        for(let i in this.state)this.temp[i] = this.state[i];
        console.log(this.temp);
        this.submit(this.state, 'basic');
        //上传basic数据
    }

    render() {
        return (
            <div className="basicInput">
                <h1 className="title">基本信息</h1>
                <div className="inputField">
                    <div className="itemName">调查员姓名：</div>
                    <input
                        className="input"
                        name='name'
                        value={this.state.name} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">职业：</div>
                    <input
                        className="input" name='occupation'
                        value={this.state.occupation} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">性格特征：</div>
                    <input
                        className="input" name='character'
                        value={this.state.character} onChange={this.handleSubmit} />
                </div>

                <div className="inputField">
                    <div className="itemName">背景故事：</div>
                    <textarea
                        className="input" name='background'
                        value={this.state.background} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">头像：</div>
                    <input
                        type='file'
                        accept='image/*' className="input" name='image'
                        onChange={this.handleImage}
                        />
                </div>
                <img id='preview' ref={this.fileInput}></img>
            </div>
        );
    }
}

export default Basic;
