import React from 'react';
import './App.css';
import Result from './result';
import Basic from './basic'
import './prop.css'
import { Academic, Social, Normal } from './prop'

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ser: 0,
            point: 5
        }
        this.callback = {
            cost: this.changePoint.bind(this),
            submit: this.handleSubmit.bind(this)
        }
        this.pageNum = 4;
    }

    changeSer(e) {
        if (this.state.ser > this.pageNum || this.state.ser < 0) return;
        if (e.target.value === 'add')
            this.setState({ ser: this.state.ser + 1 });
        else this.setState({ ser: this.state.ser - 1 });
    }

    changePoint(e, num) {
        if (this.state.point == 0 || !num) return 0;  //如果如果对象剩余点数为0或总点数为0则减小操作无效
        else {
            this.setState({ point: this.state.point - e.target.value });
            return e.target.value;
        }
    }

    handleSubmit(data, name) {
        this.setState({ [name]: data });
    }

    render() {
        var temp = [
            <Basic submit={this.handleSubmit.bind(this)} />,
            <Academic callback={this.callback} />,
            <Social callback={this.callback} />,
            <Normal callback={this.callback} />];
        return (
            <div className="App">
                <div className='inputArea'>
                    <div className='operArea'>
                        {temp[this.state.ser]}
                    </div>
                    <div className='buttonArea'>
                        {this.state.ser > 0
                            && <button className='button' value={-1} onClick={this.changeSer.bind(this)}>上一步</button>}
                        {this.state.ser < 4
                            && <button className='button' value={1} onClick={this.changeSer.bind(this)}>下一步</button>}
                    </div>
                </div>
                <Result data={this.state} />
            </div>
        );
    }
}

export default App;
