import React from 'react';
import './App.css';


class Basic extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            occupation: '',
            character: '',
            background: '',
        };
        this.submit = props.handleSubmit;
    }

    componentWillUnmount() {
        this.props.handleSubmit(this.state,'basic');
    }

    render() {
        return (
            <div className="basicInput">
                <h1 className="title">基本信息</h1>
                <div className="inputField">
                    <div className="itemName">调查员姓名：</div>
                    <input className="input" name='name' value={this.state.name} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">职业：</div>
                    <input className="input" name='occupation' value={this.state.occupation} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">性格特征：</div>
                    <input className="input" name='character' value={this.state.character} onChange={this.handleSubmit} />
                </div>
                <div className="inputField">
                    <div className="itemName">背景故事：</div>
                    <textarea className="input" name='background' value={this.state.background} onChange={this.handleSubmit} />
                </div>
            </div>
        );
    }
}

function ResultBasic(props) {
    return <div>

    </div>
}

function ResultData(props) {
    return <div>

    </div>
}

function ResultStory(props) {
    return <div>

    </div>
}
class Result extends React.Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (<div className='result'>
            <ResultBasic />
            <ResultData />
            <ResultStory />
            <div>{this.props.basic}</div>
        </div>);
    }
}

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ser: 0,
        }
        this.pageNum = 4;
        this.changeSer = this.changeSer.bind(this);
    }

    changeSer() {
        if (this.state.ser >= this.pageNum) return;
        console.log(this.state.ser);
        this.setState({ ser: this.state.ser + 1 });
    }

    handleSubmit(data, name) {
        this.setState({
            [name]: data
        })
    }

    render() {
        var temp = [<Basic submit={this.handleSubmit.bind(this)} />];
        return (
            <div className="App">
                <div className='inputArea'>
                    {temp[0]}
                </div>
                <Result data={this.state} />
                <button className='button' onClick={this.changeSer}>下一步</button>
            </div>
        );
    }
}



export default App;
