import React from 'react';
import './App.css';
import Result from './result';
import Basic from './basic'
import {Academic,Normal} from './prop'

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ser: 0,
        }
        this.pageNum = 4;
        this.changeSer = this.changeSer.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    changeSer(event) {
        if (this.state.ser > this.pageNum || this.state.ser < 0) return;
        if (event.target.className === 'button add')
            this.setState({ ser: this.state.ser + 1 });
        else this.setState({ ser: this.state.ser - 1 });
    }

    handleSubmit(data, name) {
        this.setState({[name]: data});
    }

    render() {
        var temp = [<Basic submit={this.handleSubmit} />, <p />, <p />];
        return (
            <div className="App">
                <div className='inputArea'>
                    <div className='operArea'>
                        {temp[this.state.ser]}
                    </div>
                    <div className='buttonArea'>
                        {this.state.ser > 0 && <button className='button sub' onClick={this.changeSer}>上一步</button>}
                        {this.state.ser < 4 && <button className='button add' onClick={this.changeSer}>下一步</button>}
                    </div>
                </div>
                <Result data={this.state} />
            </div>
        );
    }
}

export default App;
